<div id="fotosValidacion" class="easyui-layout" style="height: 600px; width:97%;margin: 5px;">    <div data-options="region:'west',title:'Informacion',split:true,collapsible:false"         style="width:300px;padding: 10px;">        <div class="easyui-panel" title="Busqueda" style="margin-bottom: 15px;padding: 5px;">            <form id="busquedaFotos" method="post">                <table cellpadding="5">                    <tr>                        <td><label for="fechaInicio">Fecha inicio</label></td>                        <td><input id="fechaInicio" class="easyui-datebox"                                   data-options="formatter:myformatter,parser:myparser,sharedCalendar:'#cc'"                                   placeholder="ej.02-03-2014"></td>                    </tr>                    <tr>                        <td><label for="fechaFin">Fecha fin</label></td>                        <td><input id="fechaFin" class="easyui-datebox"                                   data-options="formatter:myformatter,parser:myparser,sharedCalendar:'#cc'"></td>                    </tr>                    <tr>                        <td><label for="nombreMarca">Marca</label></td>                        <td><input id="nombreMarca" name="nombreMarca"></td>                    </tr>                    <tr>                        <td><label for="nombreTipoPromotor">Tipo de Promotor</label></td>                        <td><input id="nombreTipoPromotor" class="easyui-combobox" name="nombreTipoPromotor"                                   data-options="valueField:'idTipoUsuario',textField:'nombreTipo',url:'../php/tipo_promotor.php',required:false">                        </td>                    </tr>                    <tr>                        <td><label for="nombreExhibicion">Exhibici√≥n</label></td>                        <td><input id="nombreExhibicion" class="easyui-combobox" name="nombreExhibicion"></td>                    </tr>                    <tr>                        <td><label for="nombreTienda">Tienda</label></td>                        <td><input id="nombreTienda" class="easyui-combobox" name="nombreTienda"                                   data-options="valueField:'idTienda',textField:'nombre',required:false"></td>                    </tr>                </table>                <!-- Calendario de compartido -->                <div id="cc" class="easyui-calendar"></div>            </form>            <div style="text-align: center;padding: 5px;">                <a href="javascript:void(0)" class="easyui-linkbutton" onclick="crearDataGrid()">Aceptar</a>                <a href="javascript:void(0)" class="easyui-linkbutton" onClick="cleanForm()">Limpiar</a>            </div>            <script>                let urlglobal;                let editIndex = undefined;                $('#fechaInicio').attr('placeholder', 'dd-mm-aaaa');                $('#nombreMarca').combobox({                    valueField: 'idMarca',                    textField: 'nombre',                    url: '../php/getMarca.php',                    required: true,                    onSelect: function (record) {                        var url1 = '../php/getTiendasFotos.php?idMarca=' + record.idMarca;                        $('#nombreExhibicion').combobox('clear');                        $('#nombreTienda').combobox('clear');                        $('#nombreTienda').combobox('reload', url1);                    }                });                $('#nombreExhibicion').combobox({                    valueField: 'idExhibicion',                    textField: 'nombre',                    url: '../php/get_exhibiciones.php',                    required: false,                    onSelect: function (record) {                        var idMarca = $('#nombreMarca').combobox('getValue');                        var url = '../php/getTiendasFotos.php?idMarca=' + idMarca + '&idExhi=' + record.idExhibicion;                        $('#nombreTienda').combobox('clear');                        $('#nombreTienda').combobox('reload', url);                    }                });                function editRowFoto() {                    var row = $('#dgFotos').datagrid('getSelected');                    console.log(row);                    if (row) {                        $('#dlgFotos').dialog('open').dialog('setTitle', 'ID foto:' + row.idphotoCatalogo);                        $('#fmFoto').form('load', row);                        urlglobal = '../php/edit_foto.php?idFoto=' + row.idphotoCatalogo;                    }                }                function saveFoto() {                    $('#fmFoto').form('submit', {                        url: urlglobal,                        onSubmit: function () {                            return $(this).form('validate');                        },                        success: function (result) {                            var result = eval('(' + result + ')');                            if (result.success) {                                $('#dlgFotos').dialog('close');                                $('#dgFotos').datagrid('reload');                            } else {                                $.messager.show({                                    title: 'Error',                                    msg: result.msg                                });                            }                        }                    });                }            </script>        </div>        <div id="detalle" class="easyui-panel" title="Detalle"></div>    </div>    <!-- end west -->    <!-- start center -->    <div id="fotocenter" data-options="region:'center',title:'Fotos',split:true,collapsible:false"         style="width: auto;height: 100%;" toolbar="#toolsFoto">        <div id="toolsFoto">            <a href="javascript:void(0)" class="easyui-linkbutton" iconCls="icon-pdf" plain="true"               onClick="exportarPdf()">Exportar</a>            <a href="javascript:void(0)" class="easyui-linkbutton" iconCls="icon-edit" plain="true"               onClick="editRowFoto()">Editar foto</a>        </div>        <table id="dgFotos" style="height: 520px;"></table>        <div id="dlgFotos" title="Editar foto" class="easyui-dialog" style="width:400px;height:280px;padding:10px 20px"             closed="true" buttons="#dlgFoto-buttons">            <form id="fmFoto" method="post" novalidate>                <table>                    <tr>                        <div class="fitem">                            <td><label>id foto</label></td>                            <td><input name="idphotoCatalogo" class="easyui-validatebox" style=" width:250px" readonly>                            </td>                        </div>                    </tr>                    <tr>                        <div class="fitem">                            <td><label for="nombreSupervisor">Marca:</label></td>                            <td><input class="easyui-combobox" name="nombreMarca"                                       data-options="valueField:'idMarca',textField:'nombre',url:'../php/getMarca.php'"                                       required></td>                        </div>                    </tr>                </table>            </form>        </div>        <div id="dlgFoto-buttons">            <a href="javascript:void(0)" class="easyui-linkbutton" iconCls="icon-ok" onclick="saveFoto()">Guardar</a>            <a href="javascript:void(0)" class="easyui-linkbutton" iconCls="icon-cancel"               onclick="$('#dlgFotos').dialog('close')">Cancelar</a>        </div>    </div>    <!-- end center -->    <div id="ventanaFoto"></div>    <div id="dialogoLink" style="width: 400px;height: 200px;"></div></div>